import React, { useEffect } from "react";

import LakeCards from "./LakeCards";
import { Grid } from "@material-ui/core";
import { fetchLakes } from "../actions/lakeActions";
import { useDispatch, useSelector } from "react-redux";

const Home = () => {
	const dispatch = useDispatch();
	useEffect(() => {
		fetchLakes(dispatch);
	}, [dispatch]);
	const lakes = useSelector(state => state.lakeReducer.lakes);
	const search = useSelector(state => state.searchBarReducer.searchParams);
	const speciesFilter = useSelector(
		state => state.searchBarReducer.speciesFilter
	);

	const filterCards = () => {
		let newLakes = 
		lakes.filter(lake => {
			speciesFilter.forEach(element => {
				if (lake.species.includes(element)) {
					if (newLakes.includes(lake)) {
						return newLakes;
					} else {
						newLakes.push(lake);
					}
				}
			});
		});
		console.log(newLakes);
		return newLakes;
	};
	filterCards();
	const searchFilteredCards = lakes.filter(lake => {
		const { name } = lake;
		let searchTerm = search.toLowerCase();
		let lowerCaseName = name.toLowerCase();
		return lowerCaseName.includes(searchTerm);
	});

	return (
		<div>
			<Grid container direction="row" justify="center" alignItems="center">
				{searchFilteredCards.map(lake => {
					return <LakeCards key={lake._id} lake={lake} />;
				})}
			</Grid>
		</div>
	);
};
export default Home;
