import { FETCH_LAKES, CURRENT_LAKE, FAVORITED_LAKE } from "../actions/types";

const initialState = {
  lakes: [],
  currentLake: {},
  favoritedLakes: []
};

export default function(state = initialState, action) {
  switch (action.type) {
    case FETCH_LAKES:
      return {
        ...state,
        lakes: action.payload
      };
    case CURRENT_LAKE:
      return {
        ...state,
        currentLake: action.payload
      };
      case FAVORITED_LAKE:
      let newState = [...new Set([ ...state.favoritedLakesaction.payload])]
      return {
        ...state,
        favoritedLakes: newState
      };
    default:
      return state;
  }
}
