import React, { useEffect } from "react";

import LakeCards from "./LakeCards";
import { Grid } from "@material-ui/core";
import { fetchLakes } from "../actions/lakeActions";
import { useDispatch, useSelector } from "react-redux";

const Home = () => {
	const dispatch = useDispatch();
	useEffect(() => {
		fetchLakes(dispatch);
	}, [dispatch]);
	const lakes = useSelector(state => state.lakeReducer.lakes);
	const search = useSelector(state => state.searchBarReducer.searchParams);
	const speciesFilter = useSelector(state => state.searchBarReducer.speciesFilter)
	
	const filterCards = lakes.filter(lake=>{
		let matchingTerms = speciesFilter.map(species=>{
			lake.species.includes(species)
		})
		return lake.species.includes(matchingTerms)

	})
	console.log(filterCards, searcg)
	const searchFilteredCards = lakes.filter(lake => {
		const { name } = lake;
		let searchTerm = search.toLowerCase();
		let lowerCaseName = name.toLowerCase();
		lowerCaseName.includes(searchTerm)
		
	});
	
	return (
		<div>
			<Grid container direction="row" justify="center" alignItems="center">
				{searchFilteredCards.map(lake => {
					return <LakeCards key={lake._id} lake={lake} />;
				})}
			</Grid>
		</div>
	);
};
export default Home;
