import React from 'react';
import { connect } from 'react-redux';
import { currentLake } from '../actions/lakeActions';
import { Grid, Card } from 'semantic-ui-react'
import lakeShow from './lakeShow'

class LakeCard extends React.Component{

    state = {
        showLake: false
    }
    componentDidMount(){
       
    }
    handleClick = async(lake) =>{
        await this.props.currentLake(lake)
        this.setState({
            showLake: true
        })
    }

    render(){
        const lakes = this.props.lakes.map(lake =>(
            
                
                    <Card style={{margin:'1em', }}  onClick={ () => this.handleClick(lake) } key={`${lake._id} + 1`}>
                        <img src={lake.images[0]}  height='200px' width='auto' alt='lake' />
                        <Card.Content>
                            <Card.Header>{lake.name}</Card.Header>
                            <Card.Meta>
                                <span className='city'>{lake.locality}</span>
                            </Card.Meta>
                            <Card.Description>
                                {lake.species.join(', ')}
                            </Card.Description>
                        </Card.Content>
                        <Card.Content extra>
                        </Card.Content>
                    </Card>
                
            
        ))
        
        return(
           
                <Grid  >
                    <Grid.Row>
                       {(this.state.showLake)? <lakeShow/>:lakes.map((lake) => <div style ={{flex: '25'}}> {lake}</div>)}
                    </Grid.Row>
                </Grid>
            
          
        ) 
    }
}

const mapStateToProps = state => ({
    lakes: state.lakeReducer.lakes
})
export default connect(mapStateToProps, { currentLake })(LakeCard)
