import { FETCH_LAKES, CURRENT_LAKE, FAVORITED_LAKE } from "../actions/types";

const initialState = {
	lakes: [],
	currentLake: {},
	favoritedLakes: []
};

export default function(state = initialState, action) {
	switch (action.type) {
		case FETCH_LAKES:
			return {
				...state,
				lakes: action.payload
			};
		case CURRENT_LAKE:
			return {
				...state,
				currentLake: action.payload
			};
		case FAVORITED_LAKE:
      vnewState = [...state.favoritedLakes, action.payload]
			var flags = {};
			var newLakes = newState.filter(function(lake) {
				if (flags[lake.name]) {
					return false;
				}
				flags[lake.name] = true;
				return true;
			});
		
			return {
				...state,
				favoritedLakes: newLakes
			};
		default:
			return state;
	}
}
